import React from "react";
import BootstrapTable from "react-bootstrap-table-next";
import filterFactory, { textFilter } from "react-bootstrap-table2-filter";
import paginationFactory from "react-bootstrap-table2-paginator";
//import ToolkitProvider from "react-bootstrap-table2-toolkit";
import { Card } from "react-bootstrap";

import { useDispatch, useSelector } from "react-redux";
import { listProducts } from "../actions/productAction";
import LoadingBox from "./LoadingBox";

const products = [
  {
    id: 1,
    name: "abhishek",
    email: "abcd@gmail.com",
  },
  {
    id: 2,
    name: "abhishek",
    email: "abcd@gmail.com",
  },
  {
    id: 3,
    name: "abhishek",
    email: "abcd@gmail.com",
  },
  {
    id: 4,
    name: "abhishek",
    email: "abcd@gmail.com",
  },
  {
    id: 5,
    name: "abhishek",
    email: "abcd@gmail.com",
  },
  { id: 6, name: "abhishek", email: "abcd@gmail.com" },
];

const columns = [
  {
    dataField: "SlNo",
    text: "#",
  },
  {
    dataField: "name",
    text: "Name",
    filter: textFilter(),
    sort: true,
  },
  {
    dataField: "email",
    text: "Email",
    filter: textFilter({}),
    sort: true,
  },
  {
    dataField: "price",
    text: "Created At",
    filter: textFilter(),
  },
  {
    dataField: "price2",
    text: "Updated At",
    filter: textFilter(),
  },
  {
    dataField: "price1",
    text: "Action",
    formatter: (cell, row, rowIndex) => {
      return (
        <button
          onClick={(e) => alert(row.id)}
          className="btn btn-outline-secondary"
        >
          View
        </button>
      );
    },
  },
];

function Home() {
  const dispatch = useDispatch();

  const list = useSelector((state) => state.itemList);

  const { loading, error, itemList } = list;

  console.log("table data", itemList);

  // const { loading, error, items } = itemList;

  React.useEffect(() => {
    dispatch(listProducts());
  }, []);

  const noData = <div className="text-muted py-3 text-center"> No Data..</div>;

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-12 p-5">
          {loading ? (
            <LoadingBox />
          ) : (
            <Card>
              <BootstrapTable
                keyField="id"
                data={itemList}
                columns={columns}
                noDataIndication={noData}
                filter={filterFactory()}
                pagination={paginationFactory()}
              />
            </Card>
          )}
        </div>
      </div>
    </div>
  );
}

export default Home;
