import React from "react";
import BootstrapTable from "react-bootstrap-table-next";
import filterFactory, { textFilter } from "react-bootstrap-table2-filter";
import paginationFactory from "react-bootstrap-table2-paginator";
//import ToolkitProvider from "react-bootstrap-table2-toolkit";
import { Card } from "react-bootstrap";

import { useDispatch, useSelector } from "react-redux";
import { listProducts } from "../actions/productAction";
import LoadingBox from "./LoadingBox";

function Home() {
  const dispatch = useDispatch();

  const list = useSelector((state) => state.itemList);

  const { loading, itemList } = list;

  console.log("table data", itemList);

  // const { loading, error, items } = itemList;

  React.useEffect(() => {
    dispatch(listProducts());
  }, []);

  const columns = [
    {
      dataField: "SlNo",
      text: "#",
    },
    {
      dataField: "name",
      text: "Name",
      filter: textFilter(),
      sort: true,
    },
    {
      dataField: "email",
      text: "Email",
      filter: textFilter({}),
      sort: true,
    },
    {
      dataField: "createdAt",
      text: "Created At",
      filter: textFilter(),
    },
    {
      dataField: "updatedAt",
      text: "Updated At",
      filter: textFilter(),
    },
    {
      dataField: "#",
      text: "Action",
      align: function callback(cell, row, rowIndex, colIndex) {
       return <p className="text-center">{row}</p>;
      },
      formatter: (cell, row, rowIndex) => {
        return (
          <button
            onClick={(e) => alert(row.id)}
            className="btn btn-outline-secondary"
          >
            View
          </button>
        );
      },
    },
  ];

  const noData = <div className="text-muted py-3 text-center"> No Data..</div>;

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-12 p-5">
          {loading ? (
            <LoadingBox />
          ) : (
            <Card>
              <BootstrapTable
                keyField="id"
                data={itemList}
                columns={columns}
                noDataIndication={noData}
                filter={filterFactory()}
                pagination={paginationFactory()}
              />
            </Card>
          )}
        </div>
      </div>
    </div>
  );
}

export default Home;
