import React from "react";
import Home from "../Components/Home";
import { BrowserRouter, Route,Routes ,Link } from "react-router-dom";
import PrivateRoute from "./PrivateRoute";
import ItemScreen from "../Components/ItemScreen"
import Login from "../Components/Login";

function Router() {
  let navigate = useNavigate();


  const logoutHandle = (e) => {
    localStorage.removeItem("userInfo");
 window.location.href("/login")
  };


  return (
    <>
      <BrowserRouter>
      {console.log("local data", localStorage.getItem("userInfo"))}
      <header className="header">
        <div className="brand">
          <Link to="/">
            <h3>
              <i className="fa fa-user mx-2" aria-hidden="true"></i>
              {JSON.parse(localStorage.getItem("userInfo")).name}
            </h3>
          </Link>
        </div>
        <div className="logout">
          <Link to="#" onClick={logoutHandle} className="btn btn-outline-info">
            Logout
          </Link>
        </div>
      </header>
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/item/:id" element={<ItemScreen />} />
          <Route path="/item" element={<ItemScreen />} />
          <Route path="/" element={<Home />} exact ></Route>
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default Router;
